import React,{useEffect} from 'react'
import Blank from '../Image/blank.png'
import AOS from 'aos';
import 'aos/dist/aos.css';

function Notification() {
  useEffect(() => {
    AOS.init();
  }, [])
  return (
    <div className=' bg-slate-900 w-full h-fit p-8 md:p-28 pt-28'>
      <div>
        <span >
          <h1 className='text-white text-2xl  inline-block underline font-bold decoration-blue-600 decoration-2' data-aos="fade-top">Notification
            <svg className="h-6 w-6 pl-1 inline-block" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" aria-hidden="true">
              <path stroke-linecap="round" stroke-linejoin="round" d="M14.857 17.082a23.848 23.848 0 005.454-1.31A8.967 8.967 0 0118 9.75v-.7V9A6 6 0 006 9v.75a8.967 8.967 0 01-2.312 6.022c1.733.64 3.56 1.085 5.455 1.31m5.714 0a24.255 24.255 0 01-5.714 0m5.714 0a3 3 0 11-5.714 0" />
            </svg>
          </h1>
        </span>
      </div>
      <div className='w-full h-full' data-aos="fade-right">
        {NotificationData.map((N) => (
          <div className='bg-white rounded-xl mt-3  flex items-center justify-start p-3  border-spacing-1 w-full h-20'>
            <img src={N.IMGNOTIC} alt='' className=' w-10 h-10 rounded-full ' />
            <div className='flex flex-col items-start justify-start ml-3 '>
              <h1 className=' text-lg text-black font-bold '>{N.Title}</h1>
              <h2 className=' text-sm text-black font-normal'>{N.Des}</h2>
            </div>
          </div>
        ))}
      </div>
    </div>
  )
}

export default Notification
const NotificationData=[
  {
    IMGNOTIC: Blank,
    Title: 'Title',
    Des: "Fitness and Recreation Center",
  },
  {
    IMGNOTIC: Blank,
    Title: 'Title',
    Des: "Fitness and Recreation Center",
