import React, { useState } from "react";
// import './Event.css'; // Add your own CSS file for styling

function App() {
    const [isModalOpen, setIsModalOpen] = useState(false);

    const handleOpenModal = () => {
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
    };

    return (
        <div className="">
            <div className="EventBox w-full h-fit bg-slate-900 p-10 flex flex-col items-center justify-around">
                <h1 className='text-center text-white pt-10'>Event</h1>
                {EventData.map((E) => (
                    <div className="EventContainer w-full h-fit flex align-middle justify-evenly  flex-wrap ">
                        <div className="eventBoxCard1" data-aos="fade-right">
                            <img src={E.EventImg} alt='' className='eventcardImg' />
                            <div className="EventContent">
                                {E.description}
                            </div>
                        </div>
                        <div className="eventBoxCard2" data-aos="fade-left">
                            <img src={E.EventImg} alt='' className='eventcardImg' />
                            <div className="EventContent">
                                {E.description}

                            </div>
                        </div>
                    </div>
                ))}
            </div>
            <button onClick={handleOpenModal}>Read More</button>


            {/* Modal/Popup */}
            {isModalOpen && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <span className="close-btn" onClick={handleCloseModal}>
                            &times;
                        </span>
                        <h2>Modal Title</h2>
                        <p>
                            Detailed content goes here. You can add as much text as needed.
                        </p>
                    </div>
                </div>
            )}
        </div>
    );
}

