import Modal from 'react-modal';
import EventImg from '../Image/eventImg.jpeg'

import { X } from 'lucide-react';

const ModalExample = () => {
    const [modalIsOpen, setModalIsOpen] = useState([false, false, false, false]);

    const data = [
        { id: 1, EventImg: EventImg, description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Exercitationem facilis, quasi nemo doloremque repellendus, eligendi unde odio harum illum nesciunt eveniet aliquam sint voluptates repudiandae ea cumque ducimus, blanditiis reiciendis.Quis libero ullam obcaecati adipisci modi magnam suscipit necessitatibus corporis alias praesentium ab voluptatibus impedit velit porro officia provident ea, fuga magni harum? Cumque adipisci ex deleniti maiores delectus quo." },
