        const newModalIsOpen = [...modalIsOpen];
        newModalIsOpen[index] = false;
        setModalIsOpen(newModalIsOpen);
    };

    return (
        <div>
            {data.map((item, index) => (
                <div key={item.id} className='pt-20'>
                    <div className='bg-orange-700  rounded-xl px-20 py-10 flex flex-col gap-5 items-center mx-4'>
                        <img src={item.EventImg} className='w-80 h-80  ' alt='' />
