import React, { useState , useEffect } from 'react';
import Modal from 'react-modal';
import EventImg from '../Image/eventImg.jpeg'
import Ganesh from '../Image/ganesh.jpg'
import vishal from '../Image/vishal.jpg'
import './Event.css';
import { X } from 'lucide-react';
import AOS from 'aos';
import 'aos/dist/aos.css';

const ModalExample = () => {
    useEffect(() => {
        AOS.init();
      }, [])
    const [modalIsOpen, setModalIsOpen] = useState([false, false, false, false]);

    const data = [
        { id: 1, EventImg: Ganesh, description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. ",
        eventContent },
        { id: 2, EventImg: vishal, description: "Lorem ipsum dolor sit amet consectetur adipisicing elit." },
        { id: 3, EventImg: EventImg, description: "Lorem ipsum dolor sit amet consectetur adipisicing elit.  " },
        { id: 4, EventImg: EventImg, description: "Lorem ipsum dolor sit amet consectetur adipisicing elit.  " },
        { id: 5, EventImg: EventImg, description: "Lorem ipsum dolor sit amet consectetur adipisicing elit.  " },
    ];

    const openModal = (index) => {
        const newModalIsOpen = [...modalIsOpen];
        newModalIsOpen[index] = true;
        setModalIsOpen(newModalIsOpen);
    };

    const closeModal = (index) => {
        const newModalIsOpen = [...modalIsOpen];
        newModalIsOpen[index] = false;
        setModalIsOpen(newModalIsOpen);
    };

    return (
        <div className='w-full h-fit flex justify-center flex-wrap items-center p-20 bg-slate-900'>
            {data.map((item, index) => (
                <div key={item.id} className='p-10 '>
                    <div className='bg-white EventCard rounded-xl flex flex-col gap-5 items-center justify-center mx-4' data-aos="fade-up">
                        <img src={item.EventImg} className='EventImg rounded-xl' alt='' />
                        <div className='EventDes text-center  '>
                            {item.description}
                        </div>
                        <button className=' bg-violet-700 text-white text-center px-2 py-1 rounded-lg ' onClick={() => openModal(index)}>Open Modal</button>
                    </div>

                    <Modal
                        isOpen={modalIsOpen[index]}
                        onRequestClose={() => closeModal(index)}
                        ariaHideApp={false}
                        className='fixed inset-0 bg-opacity-30 backdrop-blur-sm  flex justify-center items-center'
                    >
                        <div >
                            <div className='mt-10 flex flex-col gap-5 text-white '>
                                <button onClick={() => closeModal(index)} className='place-self-end text-black'><X size={30} /></button>
                                <div className='bg-indigo-600 rounded-xl px-20 py-10 flex flex-col gap-5 items-center mx-4  '>
                                    <h1>ok i am</h1>
                                    <p className='max-w-md text-center'>{item.eventContent}</p>
                                </div>
                            </div>
                        </div>
                  
                    </Modal>
                </div>
            ))}
        </div>
    );
};

export default ModalExample;
