import React from 'react'
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import furniture from '../Image/furniture.jpg'
import classroom from '../Image/classroom.png'

function carousal() {
    const settings = {
        dots: true,
        infinite: true,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        speed: 500,
        autoplaySpeed: 2000,
        cssEase: "linear"
    };
  return (
    <div className="carousalBox">
    <Slider {...settings}>
        {
            data.map((d) => (
                <>
                    <div className="carousalimg">
                        <img src={d.img} className='carousalbg' alt='' />
                    </div>
                    <p className='carousaltitle'>
                        {d.title}
                    </p>
                </>
            ))
        }
    </Slider>
</div>
  )
}
const data = [
    {
        title: "furniture",
        img: furniture
    },
    {
        title: "class Room",
        img: classroom
    }
]

export default carousal
