import React from 'react';
import Homepagebg from '../Image/school-hd-1.jpg';

function HomePage() {
  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
      {/* First View */}
      <div className="col-span-1 md:col-span-1">
        <img src={Homepagebg} alt='' className='w-full h-full object-cover' />
      </div>

      {/* Second View */}
      <div className="col-span-1 md:col-span-1 flex flex-col justify-center items-center p-8">
        <p className='text-white font-bold text-2xl mb-4'>
          Welcome to Savitri Krishna Senior Secondary School
        </p>
        <p className='text-white text-lg'>
          At Savitri Krishna Senior Secondary School, we believe in providing a holistic education
          that empowers students to excel academically, socially, and emotionally.
          Our dedicated faculty, state-of-the-art facilities, and a commitment to excellence create an environment where students thrive. Explore our website to discover the various facets of our educational journey.
        </p>
      </div>
    </div>
  )}
  export default HomePage
