import React, { useState } from 'react';
import './aboutUs.css';
import Modal from 'react-modal';
import building from '../Image/building.png';

function About() {
    const [modalIsOpen, setModalIsOpen] = useState(false);

    const openAbout = () => {
        setModalIsOpen(true);
    };

    const closeAbout = () => {
        setModalIsOpen(false);
    };

    return (
        <div className="main_header py-12 bg-gray-100 h-full pt-[10%]">
            <div className="container mx-auto">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 items-center">
                    <div className="main_header_left text-center md:text-left">
                        <p>Welcome to Savitri Krishna Secondary School</p>
                        <h1>Preparing Students for <span className="text-green-700">Successful Careers</span></h1>
                        <button className="btn btn-blue mt-4" onClick={openAbout}>Check More</button>
                    </div>
                    <div className="main_header_right">
                        <img src={building} alt='' className='rounded-xl shadow-md' />
                    </div>
                </div>
            </div>
            <Modal
                isOpen={modalIsOpen}
                onRequestClose={closeAbout}
                ariaHideApp={false}
                className='modal'
            >
                <div className='modal-content'>
                    <h1 className='font-bold text-2xl md:text-lg text-white mb-4'>School Introduction</h1>
                    <p className='text-sm md:text-base text-gray-800 leading-relaxed'>Savitri Krishna Bal Vidya Mandir, established in 1999-2000 through community collaboration, aims to provide quality education to underprivileged students. Registered under the Rajasthan Organization Registration Act 1958, it's recognized by the Education Department of Rajasthan Government and Bikaner. Renowned for its facilities, the school fosters a conducive learning environment. From humble beginnings, it has flourished into a prominent educational institution, symbolizing growth akin to a mighty banyan tree. Maternal grandparents play a pivotal role in guiding students towards higher education and future careers.</p>
                    <button className="btn btn-white mt-4" onClick={closeAbout}>Close</button>
                </div>
            </Modal>
        </div>
    );
}

export default About;
