import React, { useState } from 'react';
import Modal from 'react-modal';
import EventImg from '../Image/eventImg.jpeg'

const ModalExample = () => {
  const [modalIsOpen, setModalIsOpen] = useState([false, false, false, false]);

  const data = [
    { id: 1, content: 'Modal 1 Content' ,EventImg: EventImg,description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Exercitationem facilis, quasi nemo doloremque repellendus, eligendi unde odio harum illum nesciunt eveniet aliquam sint voluptates repudiandae ea cumque ducimus, blanditiis reiciendis.Quis libero ullam obcaecati adipisci modi magnam suscipit necessitatibus corporis alias praesentium ab voluptatibus impedit velit porro officia provident ea, fuga magni harum? Cumque adipisci ex deleniti maiores delectus quo." },
    { id: 2, content: 'Modal 2 Content' },
    { id: 3, content: 'Modal 3 Content' },
    { id: 4, content: 'Modal 4 Content' },
  ];

  const openModal = (index) => {
    const newModalIsOpen = [...modalIsOpen];
    newModalIsOpen[index] = true;
    setModalIsOpen(newModalIsOpen);
  };

  const closeModal = (index) => {
    const newModalIsOpen = [...modalIsOpen];
    newModalIsOpen[index] = false;
    setModalIsOpen(newModalIsOpen);
  };

  return (
    <div>
      {data.map((item, index) => (
        <div key={item.id} className='pt-20'>
            <div className='bg-orange-700  rounded-xl px-20 py-10 flex flex-col gap-5 items-center mx-4'>
             <img src={item.EventImg} className='w-80 h-80  ' alt='' />
                 <div className='max-w-md text-center '>
                    {item.description}
                 </div>
                 <button className=' bg-violet-800 px-4 py-2 rounded-lg text-lg' onClick={() => openModal(index)}>Open Modal {item.id}</button>
             </div>
    
          <Modal
            isOpen={modalIsOpen[index]}
            onRequestClose={() => closeModal(index)}
            ariaHideApp={false}
          >
            <h2>Modal {item.id}</h2>
            <p>{item.content}</p>
            <button onClick={() => closeModal(index)}>Close Modal</button>
          </Modal>
        </div>
      ))}
    </div>
  );
};

export default ModalExample;
