import React, { useState } from 'react';
import './aboutUs.css';
import Modal from 'react-modal';
import { X } from 'lucide-react';
import building from '../Image/building.jpg';

function About() {
    const [modalIsOpen, setModalIsOpen] = useState([false, false, false, false]);

    const openAbout = (index) => {
        const newModalIsOpen = [...modalIsOpen];
        newModalIsOpen[index] = true;
        setModalIsOpen(newModalIsOpen);
    };

    const closeAbout = (index) => {
        const newModalIsOpen = [...modalIsOpen];
        newModalIsOpen[index] = false;
        setModalIsOpen(newModalIsOpen);
    };

    return (<>
       
        <div className="main_header">
            <div class="row ">
                <div class="col-md-10 col-12 mx-auto">
                    <div class="row">
                        <div class="col-md-6 col-12 main_header_left">
                            <p>welcome to weather app</p>
                            <h1>Get The latest <span>Weather</span> Info of Your City</h1>
                            <a href="/weather">
                                <button onClick={() => openAbout(index)}>check now</button>
                            </a>
                        </div>
                        <div class="col-md-6 col-12 main_header_right">
                            <img src={building} alt='' className='building' />
                        </div>
                    </div>
                </div>
            </div>
            <Modal
                isOpen={modalIsOpen[0]}
                onRequestClose={() => closeAbout(0)}
                ariaHideApp={false}
                className='fixed inset-0 bg-opacity-30 backdrop-blur-sm   flex justify-center items-center'
            >
                <div >
                    <div className='mt-10 flex flex-col gap-5 text-white '>
                        <button onClick={() => closeAbout(index)} className='place-self-end text-black'><X size={30} /></button>
                        <div className='bg-indigo-600 rounded-xl px-20 py-10 flex flex-col gap-5 items-center mx-4  '>
                            <h1>ok i am</h1>
                            <p className='max-w-md text-center'>Lorem ipsum dolor sit, amet consectetur adipisicing elit. A expedita quod velit? Incidunt officia ducimus enim necessitatibus obcaecati sequi cum nesciunt placeat molestiae dolores, modi ex quibusdam. Dicta, numquam optio!</p>
                        </div>
                    </div>
                </div>
            </Modal>
               
        </div>
      
         </>
    );
}

export default About;
