import React, { useState } from 'react';
import Modal from 'react-modal';
import EventImg from '../Image/eventImg.jpeg'
import './Event'
import { X } from 'lucide-react';

const ModalExample = () => {
    const [modalIsOpen, setModalIsOpen] = useState([false, false, false, false]);

    const data = [
        { id: 1, EventImg: EventImg, description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Exercitationem facilis, quasi nemo doloremque repellendus, eligendi unde odio harum illum nesciunt eveniet aliquam sint voluptates repudiandae ea cumque ducimus, blanditiis reiciendis.Quis libero ullam obcaecati adipisci modi magnam suscipit necessitatibus corporis alias praesentium ab voluptatibus impedit velit porro officia provident ea, fuga magni harum? Cumque adipisci ex deleniti maiores delectus quo." },
        { id: 2, EventImg: EventImg, description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Exercitationem facilis, quasi nemo doloremque repellendus, eligendi unde odio harum illum nesciunt eveniet aliquam sint voluptates repudiandae ea cumque ducimus, blanditiis reiciendis.Quis libero ullam obcaecati adipisci modi magnam suscipit necessitatibus corporis alias praesentium ab voluptatibus impedit velit porro officia provident ea, fuga magni harum? Cumque adipisci ex deleniti maiores delectus quo." },
        { id: 3, EventImg: EventImg, description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Exercitationem facilis, quasi nemo doloremque repellendus, eligendi unde odio harum illum nesciunt eveniet aliquam sint voluptates repudiandae ea cumque ducimus, blanditiis reiciendis.Quis libero ullam obcaecati adipisci modi magnam suscipit necessitatibus corporis alias praesentium ab voluptatibus impedit velit porro officia provident ea, fuga magni harum? Cumque adipisci ex deleniti maiores delectus quo." },
        { id: 4, EventImg: EventImg, description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Exercitationem facilis, quasi nemo doloremque repellendus, eligendi unde odio harum illum nesciunt eveniet aliquam sint voluptates repudiandae ea cumque ducimus, blanditiis reiciendis.Quis libero ullam obcaecati adipisci modi magnam suscipit necessitatibus corporis alias praesentium ab voluptatibus impedit velit porro officia provident ea, fuga magni harum? Cumque adipisci ex deleniti maiores delectus quo." },
        { id: 5, EventImg: EventImg, description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Exercitationem facilis, quasi nemo doloremque repellendus, eligendi unde odio harum illum nesciunt eveniet aliquam sint voluptates repudiandae ea cumque ducimus, blanditiis reiciendis.Quis libero ullam obcaecati adipisci modi magnam suscipit necessitatibus corporis alias praesentium ab voluptatibus impedit velit porro officia provident ea, fuga magni harum? Cumque adipisci ex deleniti maiores delectus quo." },
    ];

    const openModal = (index) => {
        const newModalIsOpen = [...modalIsOpen];
        newModalIsOpen[index] = true;
        setModalIsOpen(newModalIsOpen);
    };

    const closeModal = (index) => {
        const newModalIsOpen = [...modalIsOpen];
        newModalIsOpen[index] = false;
        setModalIsOpen(newModalIsOpen);
    };

    return (
        <div className='w-full h-fit flex flex-col items-center  bg-[#14161b]'>
            {data.map((item, index) => (
                <div key={item.id} className='pt-20 modalbox'>
                    <div className='bg-indigo-600  rounded-xl px-20 py-10 flex flex-col gap-5 items-center mx-4'>
                        <img src={item.EventImg} className='w-80 h-80  ' alt='' />
                        <div className='max-w-md text-center '>
                            {item.description}
                        </div>
                        <button className=' bg-violet-500 px-4 py-2 rounded-lg text-lg' onClick={() => openModal(index)}>Open Modal {item.id}</button>
                    </div>

                    <Modal
                        isOpen={modalIsOpen[index]}
                        onRequestClose={() => closeModal(index)}
                        ariaHideApp={false}
                    >
                        <div className='fixed inset-0  bg-opacity-30 backdrop-blur-sm flex justify-center items-center'>
                            <div className='mt-10 flex flex-col gap-5 text-white '>
                                <button onClick={() => closeModal(index)} className='place-self-end text-black'><X size={30} /></button>
                                <div className='bg-indigo-600 rounded-xl px-20 py-10 flex flex-col gap-5 items-center mx-4  '>
                                    <h1>ok i am</h1>
                                    <p className='max-w-md text-center'>Lorem ipsum dolor sit, amet consectetur adipisicing elit. A expedita quod velit? Incidunt officia ducimus enim necessitatibus obcaecati sequi cum nesciunt placeat molestiae dolores, modi ex quibusdam. Dicta, numquam optio!</p>
                                </div>
                            </div>
                        </div>
                  
                    </Modal>
                </div>
            ))}
        </div>
    );
};

export default ModalExample;
