import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/effect-fade';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import './Carausel.css';
import apj from '../Image/apj.png'
import swami from '../Image/swamiVivekananda.jpg'
import cv from '../Image/cv.png'
import { EffectFade, Navigation, Pagination,Autoplay } from 'swiper/modules';

export default function Carausel() {
  return (
    <>
    <div className="caraouselBody bg-slate-900">
      <h1 data-aos="fade-up">Great Thoughts</h1>
    <Swiper
    spaceBetween={30}
    effect={'fade'}
    navigation={true}
    pagination={{
      clickable: true,
    }}
    modules={[EffectFade, Navigation, Pagination,Autoplay]}
    className="mySwiper"
    autoplay={{
      delay: 1500, // Delay in milliseconds between slides
      disableOnInteraction: false, // Set to false to continue autoplay even after user interaction
    }}
  >
    {thoughtsImg.map((I) => (
      <SwiperSlide key={I.ThImg}>
        <img src={I.ThImg} alt="carouselimage" data-aos="fade-up" />
      </SwiperSlide>
    ))}
  </Swiper>
  </div>
    </>
  );
}

const thoughtsImg=[
  {
    ThImg:swami
  },
  {
    ThImg:apj
  },
  {
    ThImg:cv
  }
]
