import React, { useState } from "react";
import EventImg from '../Image/eventImg.jpeg';
import './Event.css';

function Event() {
    const [openModalIndex, setOpenModalIndex] = useState(null);

    const handleOpenModal = (index) => {
        setOpenModalIndex(index);
    };

    const handleCloseModal = () => {
        setOpenModalIndex(null);
    };

    return (
        <div className="w-full">
            <div className="EventBox w-full h-fit  p-10 flex flex-col items-center justify-around">
                <h1 className='text-center text-white pt-10'>Event</h1>
                {EventData.map((E, index) => (
                    <div key={index} className="EventContainer bg-slate-900 w-full h-fit flex align-middle justify-evenly flex-wrap">
                        <div className="eventBoxCard1 bg-white text-w" data-aos="fade-right">
                            <img src={E.EventImg} alt='' className='eventcardImg' />
                            <div className="EventContent">
                                {E.description}
                                <button onClick={() => handleOpenModal(index)}>Read More</button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>

            {openModalIndex !== null && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <span className="close-btn" onClick={handleCloseModal}>
                            &times;
                        </span>
                        <h2>Modal Title</h2>
                        <p>
                            {EventData[openModalIndex].description}
                        </p>
                    </div>
                </div>
            )}
        </div>
    );
}

export default Event;

