import React, { useState } from 'react'
import './aboutUs.css'

import building from "../Image/building.jpg";
const openAbout = (index) => {
    const newModalIsOpen = [...modalIsOpen];
    newModalIsOpen[index] = true;
    setModalIsOpen(newModalIsOpen);
};

const closeAbout = (index) => {
    const newModalIsOpen = [...modalIsOpen];
    newModalIsOpen[index] = false;
    setModalIsOpen(newModalIsOpen);
};
function About() {
    return (
        <div class="main_header">
            <div class="row ">
                <div class="col-md-10 col-12 mx-auto">
                    <div class="row">
                        <div class="col-md-6 col-12 main_header_left">
                            <p>welcome to weather app</p>
                            <h1>Get The latest <span>Weather</span> Info of Your City</h1>
                            <a href="/weather"><button>check now</button></a>
                        </div>
                        <div class="col-md-6 col-12 main_header_right">
                            <img src={building} alt='' className='building' />
                        </div>
                    </div>
                </div>
            </div>
            <Modal
                isOpen={modalIsOpen[index]}
                onRequestClose={() => closeModal(index)}
                ariaHideApp={false}
                className='fixed inset-0 bg-opacity-30 backdrop-blur-sm   flex justify-center items-center'
            >
            </Modal>
        </div>
    )
}

export default About
